tinymce.PluginManager.add("phpcode", function (e) {
    e.addCommand("InsertPHPCode", function () {
        e.execCommand("mceInsertContent", !1, "<php>echo 'example';</php>")
    }), 
	e.addButton("phpcode", {
        icon: false,
        tooltip: "Insert PHP Code",
        text: "PHP Code",
        cmd: "InsertPHPCode"
    })
});